SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span  fontweight='bold'&gt;en l'absence d'adh&lt;/span&gt;&lt;span &gt;, la membrane apicale des cellules du tcd et du tubule est impermeable a l'eau. celle-ci reste dans le tubule et le &lt;/span&gt;&lt;span  fontweight='bold'&gt;filtrat reste dilue&lt;/span&gt;&lt;span &gt;. le rein va alors eliminer une grande quantite d'urine diluee avec une osmolarite qui, si on prend en compte la petite quantite de solutes supplementaire qui peut etre reabsorbee a ce niveau, peut atteindre des valeurs tres basses, de l'ordre de &lt;/span&gt;&lt;span  fontweight='bold'&gt;50 mosmoles par litre.  &lt;/span&gt;&lt;span &gt;l'elimination d'eau en exces par le rein est appelee &lt;/span&gt;&lt;span  fontweight='bold'&gt;diurese&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
