SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;quelques mots sur la regulation de la &lt;/span&gt;&lt;span  fontweight='bold'&gt;kaliemie&lt;/span&gt;&lt;span &gt; qui s'effectue egalement dans ce dernier segment du nephron.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en effet, pres de &lt;/span&gt;&lt;span  fontweight='bold'&gt;90% du k+ est reabsorbe dans le tcp et l'anse de henle&lt;/span&gt;&lt;span &gt;. la regulation de l'homeostasie du potassium se fera donc sur les 10% qui arrivent dans le tcd et le tubule collecteur.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette regulation se fera principalement au niveau &lt;/span&gt;&lt;span  fontweight='bold'&gt;des cellules principales.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en effet les variations de la concentration plasmatique de potassium ont un &lt;/span&gt;&lt;span  fontweight='bold'&gt;effet direct sur la liberation d'aldosterone par le cortex surrenalien &lt;/span&gt;&lt;span &gt;(tel qu'illustre a la diapositive &lt;/span&gt;&lt;span  fontweight='bold'&gt;31&lt;/span&gt;&lt;span &gt;). les cellules principales pourrons ainsi corriger le desequilibre &lt;/span&gt;&lt;span  fontweight='bold'&gt;en secretant plus ou moins de potassium&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en ce qui concerne les &lt;/span&gt;&lt;span  fontweight='bold'&gt;cellules intercalaires&lt;/span&gt;&lt;span &gt;, elles ont un impact sur la kaliemie puisqu'elles &lt;/span&gt;&lt;span  fontweight='bold'&gt;reabsorbent du potassium en cas d'acidose &lt;/span&gt;&lt;span &gt;et en &lt;/span&gt;&lt;span  fontweight='bold'&gt;secretent en cas d'alcalose&lt;/span&gt;&lt;span &gt;. ces effets peuvent s'averer &lt;/span&gt;&lt;span  fontweight='bold'&gt;benefiques&lt;/span&gt;&lt;span &gt; en cas &lt;/span&gt;&lt;span  fontweight='bold'&gt;d'hypokaliemie&lt;/span&gt;&lt;span &gt; et &lt;/span&gt;&lt;span  fontweight='bold'&gt;d'hyperkaliemie&lt;/span&gt;&lt;span &gt;, &lt;/span&gt;&lt;span  fontweight='bold'&gt;respectivement&lt;/span&gt;&lt;span &gt;. par contre si la kaliemie est stable, l'action des cellules intercalaires aura &lt;/span&gt;&lt;span  fontweight='bold'&gt;un effet nefaste sur la kaliemie &lt;/span&gt;&lt;span &gt;et celle-ci devra alors etre &lt;/span&gt;&lt;span  fontweight='bold'&gt;corrigee par la regulation hormonale au niveau des cellules principales&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
