SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;si nous retournons au schema de la fonction renale que nous avions vu lors du cours d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;introduction, nous voyons qu'apres son entree dans le tubule, l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;urine primitive formee lors de la filtration glomerulaire va parcourir la totalite du tubule renal avant d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;etre excretee sous sa forme definitive. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dans les trois derniers cours de ce cycle nous allons suivre ce parcours durant lequel certaines substances seront reabsorbees et retourneront dans le sang alors que d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;autres seront secretees a partir du sang vers la lumiere du tubule. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;a l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;opposee de la filtration, qui laisse passer presque tous les composants du plasma hormis les proteines, la reabsorption et la secretion sont des processus &lt;/span&gt;&lt;span  fontweight='bold'&gt;tres selectifs&lt;/span&gt;&lt;span &gt;. nous allons voir que c&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;est &lt;/span&gt;&lt;span  fontweight='bold'&gt;grace a cette selectivite et grace a des systemes de regulation specifiques &lt;/span&gt;&lt;span &gt;que les reins controlent l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;excretion des substances independamment les unes des autres, ce qui est fondamental pour la stabilite de la composition des divers liquides de notre organisme.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
