SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;sur les 180 litres de liquide qui passent chaque jour dans le systeme des tubules, seulement 1 a 1,5 litre d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;urine est excrete. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;clic&lt;/span&gt;&lt;span &gt; : cela signifie que plus de 99% du liquide du filtrat a ete &lt;/span&gt;&lt;span  fontweight='bold'&gt;reabsorbe&lt;/span&gt;&lt;span &gt; par les cellules de la paroi des &lt;/span&gt;&lt;span  fontweight='bold'&gt;tubules&lt;/span&gt;&lt;span &gt; et est retourne dans les &lt;/span&gt;&lt;span  fontweight='bold'&gt;capillaires peritubulaires&lt;/span&gt;&lt;span &gt;. la reabsorption tubulaire est un &lt;/span&gt;&lt;span  fontweight='bold' fontstyle='italic'&gt;mecanisme de transport transepithelial&lt;/span&gt;&lt;span  fontstyle='italic'&gt; &lt;/span&gt;&lt;span  fontweight='bold'&gt; &lt;/span&gt;&lt;span &gt;qui debute des que le filtrat penetre dans le tubule.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;clic&lt;/span&gt;&lt;span &gt; : la &lt;/span&gt;&lt;span  fontweight='bold'&gt;majeure partie&lt;/span&gt;&lt;span &gt; de cette reabsorption se produit &lt;/span&gt;&lt;span  fontweight='bold'&gt;dans le premier segment du tubule&lt;/span&gt;&lt;span &gt; au niveau du &lt;/span&gt;&lt;span  fontweight='bold'&gt;tubule contourne proximal&lt;/span&gt;&lt;span &gt;, et a un niveau moindre &lt;/span&gt;&lt;span  fontweight='bold'&gt;dans l'anse de henle&lt;/span&gt;&lt;span &gt;. la reabsorption dans ces deux structures est &lt;/span&gt;&lt;span  fontweight='bold'&gt;systematique&lt;/span&gt;&lt;span &gt;, c'est-a-dire que les quantites reabsorbees sont fixes et ne seront pas modifiees par d'eventuelles influences hormonales ou nerveuses.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;clic&lt;/span&gt;&lt;span &gt; : par contre, la reabsorption qui s&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;effectue &lt;/span&gt;&lt;span  fontweight='bold'&gt;dans le dernier segment du tubule, &lt;/span&gt;&lt;span &gt;c'est-a-dire dans le tcd et le tubule collecteur, est &lt;/span&gt;&lt;span  fontweight='bold'&gt;sous regulation hormonale&lt;/span&gt;&lt;span &gt;, ce qui permet aux reins de controler de facon tres precise le volume et l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;osmolarite du plasma.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
