SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;pour etre reabsorbees dans la circulation sanguine, les substances doivent traverser &lt;/span&gt;&lt;span  fontweight='bold'&gt;deux paroi&lt;/span&gt;&lt;span &gt;s : d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;abord celle du &lt;/span&gt;&lt;span  fontweight='bold'&gt;tubule renal &lt;/span&gt;&lt;span &gt;puis la celle des &lt;/span&gt;&lt;span  fontweight='bold'&gt;capillaires peri-tubulaires&lt;/span&gt;&lt;span &gt;. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la reabsorption de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;eau et des substances dissoutes se fait grace a des transports &lt;/span&gt;&lt;span  fontweight='bold'&gt;passifs&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span  fontweight='bold'&gt;et&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span  fontweight='bold'&gt;actifs&lt;/span&gt;&lt;span &gt; similaires a ceux retrouves dans toutes les membranes de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;organisme. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les &lt;/span&gt;&lt;span  fontweight='bold'&gt;mouvements&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span  fontweight='bold'&gt;passifs&lt;/span&gt;&lt;span &gt; les plus frequemment retrouves au niveau du rein sont illustres par ces animations qui representent : a gauche la &lt;/span&gt;&lt;span  fontweight='bold'&gt;diffusion simple &lt;/span&gt;&lt;span &gt;qui s&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;effectue &lt;/span&gt;&lt;span  fontweight='bold'&gt;selon un gradient electrique ou chimique &lt;/span&gt;&lt;span &gt;et a droite la &lt;/span&gt;&lt;span  fontweight='bold'&gt;convection, &lt;/span&gt;&lt;span &gt;dans laquelle &lt;/span&gt;&lt;span  fontweight='bold'&gt;le solvant qui se deplace &lt;/span&gt;&lt;span &gt;selon un gradient de pression hydrostatique ou osmotique &lt;/span&gt;&lt;span  fontweight='bold'&gt;emporte avec lui les solutes&lt;/span&gt;&lt;span &gt;. vous pouvez en tout temps appuyer sur les boutons en-dessous des animations pour les re-visualiser.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
