SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le mecanisme responsable de la creation du gradient medullaire renal est complexe. il necessite les &lt;/span&gt;&lt;span  fontweight='bold'&gt;differences de permeabilite des deux branches &lt;/span&gt;&lt;span &gt;mais egalement la &lt;/span&gt;&lt;span  fontweight='bold'&gt;disposition anatomique en epingle a cheveu &lt;/span&gt;&lt;span &gt;de ces deux branches qui permet une circulation du liquide tubulaire dite « &lt;/span&gt;&lt;span  fontweight='bold'&gt;a contre-courant&lt;/span&gt;&lt;span &gt; ». on appelle d'ailleurs le mecanisme a l'origine de la creation du gradient medullaire renal : la &lt;/span&gt;&lt;span  fontweight='bold'&gt;multiplication par contre-courant&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
