SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;blabla importance anesthesie pour chirurgie…&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;william thomas green morton etait un dentiste americain que beaucoup le considerent comme &amp;quot;l'inventeur et decouvreur&amp;quot; de l'anesthesie. pourtant il ne fut pas le premier a utiliser l'ether pour anesthesie chirurgicale.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il fut responsable de la premiere demonstration publique reussie des qualites de l'ether en tant qu'anesthesique par inhalation en 1846.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la nouvelle methode se repandit en quelques semaines dans tous les usa, puis en europe et au-dela. des la fin de decembre 1846, la premiere amputation sous anesthesie etait realisee a londres.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
