SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;au moment de l'antiquite, je vous rappelle le role majeur d'hippocrate qui a veritablement rationnalise la pensee medicale, fait une coupure avec la pensee religieuse exclusive, propose la theorie des 4 humeurs remise en question par la suite mais qui marquait le developpement de la medecine rationnelle.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
