SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;pour rappel, une diapositive presentee dans le chapitre 1 ciblant le travail specifique sur la therapeutique mene par hippocrate a l'antiquite.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en premier lieux, retablir l'equilibre des humeurs par l'utilisation des saignees, des vomitifs, des purgatifs, des lavements dont la pratique a persiste de nombreux siecles apres l'antiquite.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;h a egalement ete precurseur dans le soulagement de la douleur. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;h a introduit des techniques de reduction de fracture et de luxation. comme on peut le voir sur l'image avec le lit hippocratique et lle developpement d'instruments permettant de pratiquer de la petite chirurgie.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
