SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;poursuivons avec un autre personnage de l'antiquite marquant de la medecine mais surtout de la therapeutique dont nous avons deja parle, il s'agit de galien. mais juste avant un detour par dioscoride qui l'a precede et dont galien s'est inspire egalement.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
