SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;pour rappel, vu au chapitre 1, dioscoride personnage de la rome antique du 1&lt;/span&gt;&lt;span &gt;er&lt;/span&gt;&lt;span &gt; siecle a redige un ouvrage majeur de therapeutique, le traite des matieres medicales ou de materia medica. il definit la theorie des signatures, theorie originale basee sur un concept de similitude entre la forme, la structure d'un element vegetal, animal ou mineral et les organes. ainsi comme vous pouvez le voir sur la monographie des plantes dont la forme peut rappeler le cœur sont considerees comme a action cardiaque ou des plantes qui se pelent comme l'oignon ou des animaux a ecailles comme le poisson se voient attribuer des vertues desquamantes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@galien @";
SDpagedocid[SDpagenum] = SDdocnum;
