SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;c'est a cette epoque que l'on voit egalement apparaitre la premiere separation de la medecine et la pharmacie ou des secteurs des hopitaux, les bimaristan, sont reserves pour le stockage et la preparation des remedes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;c'est razes qui le premier contribue a singulariser la pharmacie en tant que metier specifique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;egalement a noter l'influence majeure d'avicenne, medecin auteur du canon de la medecine, ouvrage de base de l'enseignement des methodes medicales de l'epoque qui persistera plusieurs siecles.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;son influence est illustree par le blasons toujours actuel de la societe royale de pharmacie britannique&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
