SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;voila nous arrivons a la fin de l'histoire de la therapeutique au cours de la periode medievale.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;permettez moi un petit intermede local sur ce theme en attirant votre attention sur le jardin que vous traversez sans doute quotidiennement sur le site de la faculte de medecine-pharmacie de grenoble.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cree en 2014 par notre collegue serge krivobok enseignant-chercheur a la faculte de pharmacie, ce jardin est appele jardin medieval dominique villars du nom d'un medecin de l'hopital de grenoble qui se passionna pour la botanique et l'usage des plantes en therapeutique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;serge krivobok a mis a l'honneur l'utilisation therapeutique des plantes en faisant notamment a la theorie des 4 humeurs selon galien.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;je vous invite a cliquer sur le lien hypertexte du bas de la diapositive pour visualiser un cours reportage sur l'histoire de la pharmacie hospitaliere et sur le jardin botanique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
