SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;une avancee remarquable au cours de cette periode a egalement ete l'evolution de la chirurgie sous l'impulsion d'ambroise pare qui a fait passe cet exercice considere comme secondaire et manuel a une veritable discipline medicale dont nous mesurons aujourd'hui les impacts en matieres d'innovation medicale.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
