SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;abordons maintenant une des premiere utilisation therapeutique de ce que l'on peut qualifier de medicament, la quinine ou quinquina utilisee depuis le 17 eme siecle dans le traitement du paludisme.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
