SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;au moyen-age, les metiers de marchand et d'artisanat se regroupaient sous forme de corporation conferant ainsi une situation de monopole d'activite. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la corporation d'apothicaire etait generalement bien etablie et socialement reconnue bien que non reconnue par l'universite.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour la formation, l'apothicaire suivait alors les regles des corporations sous forme de compagnonnage et devait realiser des preparations galeniques.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une chaire de pharmacie et de chirurgie fut mise en place au sein de l'universite de medecine de montpellier en premier avant qu'en 1777 une declaration royale de louis xvi a cree le college de pharmacie. les apothicaires prennent alors le nom de pharmaciens et obtiennent le monopole de la vente des medicaments.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'enseignement pharmaceutique, si longtemps en butte aux oppositions des medecins, est enfin organise officiellement avec la creation de la maitrise de pharmacie reconnaissant ainsi un savoir-faire associe a un savoir specifique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette declaration marque la veritable naissance de la pharmacie moderne.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
