// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@les cibles et la selectivite des medicaments@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide1.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;il est difficile de parler de cibles et de medicaments sans mentionner paul ehrlich, personnage que vous avez rencontre dans le cours portant sur l'histoire de la pharmacie du pr. patrice trouiller au premier semestre et qui a recu le prix nobel de medecine en 1908 (je parle de paul ehrlich, pas de patrice troullier) pour ses travaux portant sur l'immunologie.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il propose, a la suite de travaux portant sur les colorants, que &lt;/span&gt;&lt;span &gt;l&lt;/span&gt;&lt;span &gt;es substances n'agissent pas si elles ne sont pas fixees.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'effet du medicament est initie, en general, par sa liaison a une macromolecule de l'organisme : ce sera la cible moleculaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;nous allons d'emblais mentionner les exceptions a cette regle.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@les exceptions@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide2.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;en effet, quelques medicaments echappent a la regle de la liaison a une cible moleculaire exprimee par l'homme.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;parmi ces medicaments il convient de citer les modificateurs du ph sanguin ou gastrique : ils agissent en tamponnant les protons au niveau plasmatique ou dans la lumiere de l'estomac&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;nous trouvons les laxatifs osmotiques et les laxatifs de lest qui vont attirer l'eau dans la lumiere de l'intestin de facon a faciliter l'evacuation des matieres fecales.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;nous trouvons aussi une resine chelatrice des sels bilaires qui va par ce mecanisme physico-chimique de formation d'un complexe limiter l'effet emulsionnant des lipides assure par les sels biliaires et donc limiter l'absorption du cholesterol alimentaire ou encore les agents de chelation des ions utilises dans certaines intoxications.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;nous trouvons encore les anti-corps complexant un mediateur et empechant la fixation de ce mediateur a sa cible moleculaire,&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;nous trouvons, enfin, les anti-infectieux : agents qui ont pour fonction de detruire les virus, les bacteries ou les parasites en interagissant avec une cible exprimee par ces vecteurs de maladies et devant idealement etre sans aucun effet sur les macromolecules humaines.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide3.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;maintenant que nous avons ecarte les exceptions, revenons sur ce qu'est une cible pour un medicament.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;toute proteine de l'organisme est un site de liaison potentiel de medicament : de bonnes connaissances en biologie cellulaire sont utiles au pharmacologue. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;si cette liaison modifie la fonction de cette proteine et que cette modification conduit a une reponse cellulaire a l'origine de l'effet recherche, la proteine est consideree comme etant la &lt;/span&gt;&lt;span &gt;cible&lt;/span&gt;&lt;span &gt; du medicament. il faut donc connaitre la fonction de la proteine et disposer de techniques d'etude de cette fonction.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide4.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;pour comprendre l'effet therapeutique d'un medicament il faut connaitre sa cible, le role physiologique de cette cible, les mecanismes biochimiques qui engendrent la reponse de la cellule (les voies de signalisation ou de transduction du signal).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour quantifier l'effet d'un medicament il faut pouvoir mesurer la reponse cellulaire (contraction…) ou la modification du fonctionnement d'un tissu ou d'un organe (vasoconstriction …) ou encore la modification d'une fonction physiologique de l'organisme (modulation de la pression arterielle …).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@mesure de la reponse d'un organe isole@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide5.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;voici un exemple de quantification de reponse cellulaire induite par l'action d'un agent appele ici agoniste qui va entrainer une contraction musculaire mesurable par une technique appropriee.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;plus on augmente les doses et plus l'effet est important.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous reviendrez sur ce type de courbe nommee courbe dose-reponse dans le cours du dr fracoise stanke.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
