SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la selectivite est une notion essentielle pour un medicament dont elle conditionne l'activite therapeutique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les aspects theoriques de la selectivite seront developpes dans le cours du dr francoise stanke.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;retenez qu'aucun medicament n'est specifique d'une cible biologique : pour s'en convaincre il suffit d'augmenter la dose administree pour voir apparaitre d'autres effets que ceux recherches initialement, effets qualifies alors de secondaires ou d'indesirables ou meme de toxiques.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un des premiers toxicologues, paracelse, a meme etendu ce concept de toxicite a toutes les substances. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;a titre d'exemple : meme une consommation excessive d'eau la plus pure qui soit peut s'averer toxique, phenomene d'hyperhydratation.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
