SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;avant de parler de diversite des medicaments je vous propose de passer une minute sur la definition legale francaise du medicament. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous reviendrez plus en detail avec le dr martine deletraz sur cette definition dans le 3&lt;/span&gt;&lt;span &gt;eme&lt;/span&gt;&lt;span &gt; cycle de cette unite d'enseignement portant sur l'initiation a la connaissance du medicament.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;je vous propose ici une version tronquee pour axer mon propos sur les seuls medicaments utilises en medecine humaine et pour souligner le fait que le medicament pourra etre utile au diagnostic medical ou a la correction de fonctions physiologiques par une action pharmacologique, immunologique ou metabolique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
