SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;revenons aux principes actifs eux-memes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les presque 1200 principes actifs se repartissent en :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;molecules d'origine vegetale et analogues de synthese avec par exemple l'atropine, la morphine ou la digoxine&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;molecules de synthese chimique, selectionnees par tri pharmacologique (screening ou depistage)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;molecules d'origine humaine et analogues de synthese chimique avec par exemples les hormones ou les neuromediateurs&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;et enfin les macromolecules d'origine humaine produites par genie genetique : c'est l'exemple des hormones peptidiques ou des anticorps monoclonaux et autres enzymes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
