SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;il est difficile de parler de diversite des molecules chimiques utilisees comme principe actif de medicaments sans evoquer en quelques diapositives le dossier de la selection chirale. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la stereoisomerie est un concept qui decrit des structures de meme constitution mais qui different par leur arrangement spatial. selon cetr arrangement nous parlerons d' enantiomere s ou r &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
