SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;ces enantiomeres sont &lt;/span&gt;&lt;span &gt;deux molecules images, l'une de l'autre dans un miroir, non superposables.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il semble raisonnable de penser qu'il est difficile de mettre son pied droit dans sa chaussure gauche et inversement.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il est aussi raisonnable de penser qu'un principe actif qui doit s'ancrer a sa cible pour exercer son effet le fera de facon plus efficace dans une conformation plutot que dans l'autre.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
