SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;pour realiser ce cours j'ai utilise des ouvrages de references dont les deux qui sont indiques sur cette diapositive.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il n'est pas utile, bien entendu, d'acheter des cette annee ces ouvrages ni meme de les consulter systematiquement afin de vous preparer a l'epreuve de classement. je vous indique leur existence pour que vous puissiez dans les annees futures les consulter en cas de besoin.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
