SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;une question des de savoir maintenant sur combien de cibles vont agir les quelques 1200 agents therapeutiques utilises aujourd'hui en france.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la reponse est de l'ordre de 330 cibles.  &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;parmi ces 330 cibles environ 270 sont des proteines codees par le genome humain et le reste par les organismes pathogenes comme les virus, les bacteries et les parasites.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;comment expliquer ce rapport d'environ 4 agents pour 1 cible ? &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une premiere explication est qu'il peut exister plusieurs agonistes pour un meme recepteur (ou plusieurs inhibiteurs pour une meme enzyme).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces agents auront par exemple une selectivite differente pour la cible et ils pourront presenter des effets indesirables differents. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dans une classe therapeutique donnee le chez de file est souvent le premier agent a avoir ete commercialise et c'est souvent aussi celui qui a la moins bonne selectivite.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une autre explication est que pour une meme cible certains agents therapeutiques seront des agonistes alors que d'autres seront des antagonistes. vous verrez dans le detail avec francoise stanke ce que regroupent ces notions et toutes les nuances quelles peuvent avoir mais pour faire simple un agoniste va activer la cible alors qu'un antagoniste va empecher son activation.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@la diversite des cibles@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide2.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;¼ de ces 330 cibles correspondent a des enzymes et un autre ¼ correspondent a des recepteurs couples a des proteines g.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les recepteurs membranaires non couples aux proteines g et les recepteurs nucleaires se partagent un 3&lt;/span&gt;&lt;span &gt;eme&lt;/span&gt;&lt;span &gt; quart.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les canaux ioniques, pompes ioniques et les transporteurs membranaires representent 15% de ces cibles. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on peut remarquer que plus de 50 des cibles exploitees sont des proteines de la membrane plasmique des cellules qui vont pouvoir modifier l'activite de ces cellules sans meme y penetrer.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;enfin, notez que seulement 5% des cibles de medicaments utilises couramment restent inconnues. ce chiffre peut paraitre important lorsque on considere que depuis de nombreuses annees il est exige pour tout nouveau medicament qui arrive sur le marche d'avoir identifie sa cible.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on pourrait se dire que ces medicaments non plus leur place : ce serait une attitude peu raisonnable car il faudrait pour cette raison apprendre a se passer par exemple du paracetamol dont nous avons parle il y a quelques diapositives. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
