SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;ce schema tente d'illustrer quelques unes des cibles des medicaments.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il va nous servir de fil conducteur pour les prochaines diapositives. il peut sembler complexe mais je sais que certaines des notions qui figurent sur ce schema ont d'ores et deja etaient abordees durant le premier semestre.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
