SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;nous allons dans un deuxieme temps aborder les recepteurs canaux. ils font partis des recepteurs membranaires. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il s'agit d'une famille des proteines qui sont responsables des equilibres ioniques transmembranaires.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;a cote des recepteurs canaux il existe des canaux actives par autre chose que des ligands, mais aussi des pompes, des echangeurs et des transporteurs.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les bases de cette signalisation ionique ont ete etudiees notamment dans les cours de biophysique : vous savez deja que dans les cellules eucaryotes le distribution ionique de part et d'autre de la membre est inegale.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@les recepteurs canaux @";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
