SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la superfamille des recepteurs-canaux comporte peu de membres mais ces membres possedent des fonctions physiologiques importantes et sont des cibles therapeutiques interessantes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la permeabilite ionique associe aux recepteurs-canaux peut etre anionique ou cationique. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;parmi les recepteurs canaux a permeabilite cationique nous trouvons les recepteurs nicotiniques de l'acetylcholine. ce sont ces recepteurs que je vais utiliser comme exemple pour illustrer les recepteurs canaux.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; parmi les recepteurs canaux a permeabilite anionique nous trouvons par exemple les recepteurs gaba-a.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

