SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;voyons donc les recepteurs nicotiniques &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'acetylcholine se lie a deux familles de recepteurs les recepteurs nicotiniques notes nachr et les recepteurs muscariniques notes machr qui sont pour leur part des recepteurs a 7 domaines transmembranaires.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la permeabilite est cationique nous l'avons dit mais il est apparu que les permeabilites sodique et calcique sont predominantes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les recepteurs nachr sont des pentameres constitues de 4 types differents de monomeres : le monomere alpha est toujours en double.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;chaque monomere presente 4 helices transmembranaires : vous avez dans la partie inferieure une representation d'un monomere.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;chez les mammiferes 9 sous unites alpha et 4 beta ont ete clonees ce qui laisse presager l'existence de nombreuses isoformes de ce recepteur. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;deux types de localisations sont rapportes : une localisation nerveuse (incluant le sn central et le sn peripherique) et une localisation musculaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
