SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;ce schema illustre le fonctionnement d'une synapse cholinergique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un potentiel d'action parcourt le neurone pre-synaptique ce qui a comme consequence de liberer dans la fente synaptique l'ach.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce mediateur va aller se fixer sur les recepteurs nachr exprimes par le neurone post-synaptique declenchant l'entree de na+ et de ca++ dans cette cellule nerveuse ce qui se traduit par un potentiel postsynaptique excitateur note ppse.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un exemple de ce type de synapse est decrit dans les ganglions sympathiques comme dans les ganglions parasympathiques. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
