SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la meilleure connaissance de ces recepteurs nicotiniques alpha4-beta2 a conduit a la mise sur le marche en 2006 de la varenicline dans une specialite nommee champix ® et utilisee dans le sevrage tabagique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous auriez bien entendu fait le lien depuis plusieurs diapositives entre le noms de ces recepteurs et la nicotine, contenue dans le tabac.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la nicotine est en effet un agoniste de ces recepteurs, agoniste exogene, alors que l'ach est l'agoniste endogene.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette nicotine serait a la base des proprietes addictives du tabac via la stimulation des recepteurs nicotiniques alpha4-beta2.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la varenicline va occuper ces recepteurs mais sans entrainer une reponse maximale reduisant ainsi les symptomes de manque qui accompagne le sevrage tabagique. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;des etudes sont en cours sur la capacite de ce medicament a aider les patients dependant a l'alcool. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
