SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les recepteurs a 7 domaines transmembranaires couples aux proteines g sont aussi notes plus simplement rcpg.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce sont les cibles d'environ 1/3 des medicaments actuellement utilises en therapeutique, medicaments qui seront des agonistes ou antagonistes de ces recepteurs.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les rcpg sont presents dans toutes les cellules de notre organisme.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il faut souligner des ca present les recepteurs couples aux proteines g mais qui ne possedent pas  7 domaines transmembranaires et remarquer que par ailleurs des recepteurs a 7 domaines transmembranaires pourront interagir avec d'autres proteines que les proteines g trimeriques. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
