SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;c'est le cas par exemple des recepteurs de la serotonine notes 5-ht.  ce sera aussi le cas pour les recepteurs des catecholamines, de l'acetylcholine …&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;voici une famille de recepteurs dont le ligand endogene est un neuromediateur la serotonine ou 5 hydroxy-triptophane.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette famille comprend 14 membres : 1 recepteur canal et 13 recepteurs de la famille rcpg.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
