SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;plusieurs representations schematiques sont proposees pour ces rcpg : 3 sont illustrees ici.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;elles mettent l'accent sur le nombre de domaines transmembranaires, sur le fait que la partie n-terminale est extracellulaire et que la partie  terminale est intracellulaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cependant ces representations n'illustrent pas le fait que le nombre de residus amino-acyls des rcpg varie de 290 a plus de 1200.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
