SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le couplage avec les proteines g trimeriques est represente sur ce schema ainsi que les principaux ligands.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;plusieurs effecteurs pourront assurer la transduction du signal : ce pourra etre une proteine cytosolique a activite enzymatique comme la phospholipase c, ou encore une proteine transmembranaire a activite enzymatique aussi comme l'adenylate cyclase, ou enfin un canal ionique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;nous reviendrons plus tard sur la structure des proteines g.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
