SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;compte tenu du nombre de representants de cette super famille de recepteurs il a ete propose une classification basee sur l'analogie de structure primaire et sur les sites d'interactions avec les agonistes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la famille est divisee en 3 groupes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le groupe 1a reunit les recepteurs des petits ligands, des petits neuromediateurs comme l'adrenaline, la noradrenaline, la dopamine, la serotonine (dont nous venons de parler), l'acetylcholine (pour ce qui est des recepteurs muscariniques, les opiaces … ainsi qu'une partie des recepteurs gustatifs et olfactifs. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le groupe 1b est celui des recepteurs des hormones peptidiques et des neuropeptides notamment.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le groupe 1c est le groupe des recepteurs des hormones glycoproteiniques.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour la famille 2 ce sont les recepteurs des hormones proteiniques de masse importante : glucagon ou calcitonine par exemple.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour la famille 3 ce sont le recepteur du ca++ extracellulaire, le recepteur gaba-b et les recepteurs metabotropes du glutamate.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
