SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les proteines g &lt;/span&gt;&lt;span &gt;trimeriques&lt;/span&gt;&lt;span &gt; sont constituees comme leur nom l'indique de 3 sous unites :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une sous unite alpha qui presente une grande diversite et un dimere considere come indissociable d'un point de vue fonctionnel : le dimere beta-gamma.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la classification des proteines g est basee sur les analogies de structure des sous-unites alpha  :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la famille &lt;/span&gt;&lt;span &gt;gs&lt;/span&gt;&lt;span &gt; comprend les proteines dont la sous-unite alpha est &lt;/span&gt;&lt;span &gt;alpha-s&lt;/span&gt;&lt;span &gt; qui stimulent l'&lt;/span&gt;&lt;span &gt;adenylate&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;cyclase&lt;/span&gt;&lt;span &gt; (notee ac sur ce schema). &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous voyez que d'autres effecteurs pourront intervenir dans la transduction du signal initie par la liaison d'un ligand sur un rcpg de type &lt;/span&gt;&lt;span &gt;gs&lt;/span&gt;&lt;span &gt; mais je ne vous demande de retenir que l'exemple de la stimulation (d'ou le s) de l'&lt;/span&gt;&lt;span &gt;adenylate&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;cyclase&lt;/span&gt;&lt;span &gt;. ceci va se traduire par une augmentation de la concentration intracellulaire d'&lt;/span&gt;&lt;span &gt;ampc&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la famille gi comprend les proteines dont la sous-unite alpha est &lt;/span&gt;&lt;span &gt;alphai&lt;/span&gt;&lt;span &gt; ou encore alpha 0 : elles inhibent l'&lt;/span&gt;&lt;span &gt;adenylate&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;cyclase&lt;/span&gt;&lt;span &gt;. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la encore d'autres effecteurs pourront intervenir dans la transduction du signal initie par la liaison d'un ligand sur un rcpg de type gi ou g0 mais je ne vous demande de retenir que l'exemple de l'inhibition (d'ou le i) de l'&lt;/span&gt;&lt;span &gt;adenylate&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;cyclase&lt;/span&gt;&lt;span &gt;. ceci va se traduire par une diminution de la concentration intracellulaire d'&lt;/span&gt;&lt;span &gt;ampc&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la famille &lt;/span&gt;&lt;span &gt;gq&lt;/span&gt;&lt;span &gt; comprend les proteines dont la sous-unite alpha est alpha q ou alpha 11 : elles stimulent les &lt;/span&gt;&lt;span &gt;phospholipases&lt;/span&gt;&lt;span &gt; c.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;enfin, la famille g12 dont l'effecteur principal est represente par les petites proteines g qui ont des caracteristiques communes avec la sous-unite alpha des proteines g &lt;/span&gt;&lt;span &gt;trimeriques&lt;/span&gt;&lt;span &gt;. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;a ces differents couplage s'ajoute la possibilite qu'a le dimere beta-gamma d'initier plusieurs voies de &lt;/span&gt;&lt;span &gt;transdution&lt;/span&gt;&lt;span &gt;. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
