SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;nous allons maintenant traiter un exemple classique de rcpg au travers des recepteurs aux opioides. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le nom d' opioides est attribue a des neuropeptides endogenes qui ont des effets semblables a ceux d'un alcaloide du pavot : la morphine.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces opioides assurent la transmission nerveuse dans la paroi intestinale, la moelle epiniere et le systeme limbique en se liant a des rcpg.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le principal role de ces peptides endogenes est le controle de la perception de la douleur. cette perception implique toutefois de nombreux mediateurs et il sera possible de controler la nociception en interferant avec d'autres cibles que les recepteurs aux opioides.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
