SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la nomenclature des recepteurs aux opioides fait reference a 4 types de recepteurs de la famille 1a des rcpg.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les recepteurs mu (ou mop selon la nouvelle nomenclature peu utilisee) presentent entre 50 et 70% d'homologie avec les 3 autres types de rx.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ils sont tres largement representes aussi bien au niveau du snc qu'au niveau intestinal.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il semble que les effets antalgiques de la morphine soient essentiellement sous la dependance de l'activation de ces recepteurs. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la dependance physique serait helas elle aussi sous la dependance de l'activation de ces recepteurs.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
