SDpagedisplay[SDpagenum] = "Slide3.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;ces canaux ioniques sont dits « voltage-dependants » ou dependants du potentiel. ce sont ces canaux qui sont dans les cellules excitables responsables de la phase de depolarisation rapide du potentiel d'action. vous avez rencontres ces canaux dans l'excellent cours de physiologie cardiaque au chapitre 1 portant sur l'activation rythmique de la contraction cardiaque.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces canaux permettent le passage des ions dans le sens du gradient de concentration c'est-a-dire du compartiment le plus concentre vers le compartiment le moins concentre. il est evident qu'un tel fonctionnement ne pourra se repeter que si d'autres structures transmembranaires sont capables de retablir les desequilibres.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;voici un schema emprunte au cours du pr bernard muller qui enseigne a bordeaux la pharmacologie, et ou sont illustrees ces notions de gradients ioniques et de presence au cote des canaux, de pompes et de transporteurs.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
