SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les principales caracteristiques de ces acteurs sont decrites dans ce tableau : vous retiendrez que la capacite de transfert ionique nommee conductance est elevee pour les canaux contrairement aux autres structures. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous retiendrez aussi que l'activite de ces canaux peut etre modulee par des phophorylations ou des messagers intracellulaires.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
