SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les canaux sodiques dependants du potentiel membranaire sont localises a la membrane plasmique des cellules excitables ou automatiques, et ont un role dans l'initiation et la propagation du potentiel d'action. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la nomenclature de ces recepteurs est basee sur les sigles na pour sodium et v pour voltage.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
