SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la structure des canaux sodiques dependants du voltage est illustree sur cette diapositive.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ils sont constitues d'une grande sous-unite alpha associee a des petites sous-unites beta dont le role semble mineur. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la sous-unite alpha comprend 24 helices transmembranaires regroupees en 4 domaines homologues, la 4&lt;/span&gt;&lt;span &gt;eme&lt;/span&gt;&lt;span &gt; helices de chaque domaine interviendrait comme detecteur de voltage. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
