SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;une quinzaine d'isoformes de la sous-unite alpha ont ete clonees : la famille nav 1 est la mieux etudiee avec 9 membres dont la localisation tissulaire principale est rapportee ici. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;par exemple, le muscle squelettique exprime majoritairement nav 1.4&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;diverses mutations des canaux sodiques sont responsables de maladies genetiques affectant les muscles et les neurones. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
