SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;de nombreuses toxines affectent les canaux calciques avec des consequences essentiellement neuronales. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la tetrodotoxine ou ttx, isolee de tissus de poissons spheriques : les tetrodons ou fugu est un bloqueur de ces canaux.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la saxitoxine ou stx produite par un dinoflagelle marin : &lt;/span&gt;&lt;span  fontstyle='italic'&gt;gonyaulax catenella &lt;/span&gt;&lt;span &gt;qui se retrouvent dans des coquillages comme les clams, est aussi un bloqueur.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la &lt;/span&gt;&lt;span &gt;batrachotoxine produite par des grenouilles d'amerique du sud dont phyllobates aurotaenia, est un activateur irreversible des canaux sodiques et la substance la plus toxique du regne animal.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces substances ont permis l'etude de ces canaux sodiques mais n'ont pas d'utilisation therapeutique. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

