SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la plus grande famille de canaux ioniques est celle des canaux potassiques : on en connait aujourd'hui environ 70 representants.  &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ils interviennent dans la phase de repolarisation des cellules excitables et des cellules automatiques. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la nomenclature est calquee sur celle des canaux sodiques : le principal ion transporte et le principal regulateur physiologique du canal sont a la base de cette nomenclature.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;leur diversite structurale est importante et leur mecanisme de regulation plus complexe que dans le cas des canaux sodiques.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

