SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;bien entendu cette cible est exploitee : la sous unite sur devant son nom a ces medicaments: les sulfonylures. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'utilisation de ces medicaments est meme anterieure a la description des canaux katp. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;sur cette diapositive il est question du glibenclamide qui va fermer ces canaux independamment de la concentration intracellulaire d'atp.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette fermeture va depolariser la membrane cellulaire, ouvrir les canaux calciques dependants du voltage, et le calcium va favoriser l'exocytose d'insuline.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;tout ceci ne peut fonctionner que si les cellules ont conserve la capacite de synthetiser l'insuline.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
