SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;parlons maintenant des canaux calciques. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;nous allons nous concentrer comme prevu sur les canaux de la membrane plasmique. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la difference avec les conductance sodique et potassique est que le calcium  a un role de messager intracellulaire : lorsque sa concentration intracellulaire augmente il se fixe a des proteines specialisees dans sa reconnaissance comme la calmoduline, la troponine c ou les annexines pour modifier l'activite d'autres proteines ou de divers constituants intracellulaires.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le calcium sera ainsi responsable du couplage entre l'excitation et la contraction musculaire pour ce qui est du muscle lisse et du muscle cardiaque ou encore du couplage entre l'excitation et la secretion de mediateurs par exemple.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

