SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la structure de ces canaux est complexe : une sous-unite principale alpha 1 forme le pore. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette sous unite est de structure voisine de la sous unite alpha des canaux sodiques : 4 domaines transmembranaires de 6 helices chacun.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;plusieurs sous unites sont associees a alpha-1 : &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la sous-unite alpha-2 representee en haut a droite est extracellulaire est rattachee par un pont disulfure a une sous-unite delta transmembranaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la sous-unite beta est elle intracellulaire et en fin la sous unite gamma qui est transmembranaire..&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour couronner le tout : 10 sous unites alpha-1 sont codees par 10 genes distincts, ainsi que 4 sous unites beta, 5 gamma et 3 ensembles alpha2-delta.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
