SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la nomenclature des canaux calciques rend compte de la diversite des sous-unites. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'association a cav d'une combinaison de chiffres informant sur la famille de gene et sur l'isoforme permet de decliner ces canaux en cav 1.1. a cav 3.3.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;parmi ces canaux la famille 1 correspond aux canaux bloques par des molecules chimiques qui sont les dihydropyridines. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces canaux sont aussi nommes canaux calciques de type l car une fois actives par la depolarisation membranaire ils vont rester ouverts longtemps (long lasting) ou pour d'autres auteurs ils vont s'inactiver lentement (low inactivation) : les deux moyens mnemotechniques fonctionnent avec l pour long ou pour low.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;des mutations de ces canaux seront le support de maladies genetiques qui affecteront le muscle ou la vision. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
