SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;ce sont eux qui seront responsables pour les cellules cardiaques de l'entree du ca++ et donc du couplage excitation contraction.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous reconnaitrez sur ce schema les tubules t pour tubules transverses, vous noterez la proximite du reticulum endoplasmique formant des tubules l pour tubules longitudinales et enfin les proteines contractiles. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;lorsqu'ils seront exprimes par les cellules musculaires lisses vasculaires ils interviendront aussi dans les resistances peripheriques vasculaires.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
